import styled from "@emotion/styled";

export const ContainerStyled = styled.div`
  margin-left: auto;
  margin-right: auto;

  @media screen and(min-width:480px) {
    width: 480px;
    padding: 0 20px;
  }

  @media screen and(min-width:768px) {
    width: 768px;
    padding: 0 33px;
  }

  @media screen and(min-width:1280px) {
    width: 1280px;
    padding: 0 16px;
  }
`;
